import './App.css';
import React, { useState, useEffect } from "react";
import Axios from 'axios';

function App() {
    const [teamName, setTeamName] = useState('');
    const [capRoom, setCapRoom] = useState('');
    const [total_money_spent, setTotal_money_spent] = useState('');
    const [draft_picks, setDraft_picks] = useState('');

    const [dTeamName, setTeamNameDelete] = useState('');

    const [teamList, setTeamList] = useState([]);
    const [query1List, setQuery1List] = useState([]);
    const [query2List, setQuery2List] = useState([]);

    const [findTeam, setFindTeam] = useState('');
    const [findTeamArray, sendFindTeamArray] = useState([]);

    useEffect(() => {
        Axios.get('http://localhost:3002/api/get').then((response) => {
            setTeamList(response.data)
        })
    }, [])

    useEffect(() => {
        Axios.get('http://localhost:3002/api/query1').then((response) => {
            setQuery1List(response.data)
        })
    }, [])

    useEffect(() => {
        Axios.get('http://localhost:3002/api/query2').then((response) => {
            setQuery2List(response.data)
        })
    }, [])

    const Tabs = React.createClass({
        displayName: 'Tabs',
        getDefaultProps() {
          return {
            selected: 0
          };
        },
        getInitialState() {
          return {
            selected: this.props.selected
          };
        },
        handleClick(index, event) {
          event.preventDefault();
          this.setState({
            selected: index
          });
        },
        _renderTitles() {
          function labels(child, index) {
            let activeClass = (this.state.selected === index ? 'active' : '');
            return (
              <li>
                <a href="#">
                  {child.props.label}
                </a>
              </li>
            );
          }
          return (
            <ul>
              {this.props.children.map(labels.bind(this))}
            </ul>
          );
        },
        _renderContent() {
          return (
            <div>
              {this.props.children[this.state.selected]}
            </div>
          );
        },
        render() {
          return (
            <div>
              {this._renderTitles()}
              {this._renderContent()}
            </div>
          );
        }
      });

      const Pane = React.createClass({
        displayName: 'Pane',
        propTypes: {
          label: React.PropTypes.string.isRequired,
          children: React.PropTypes.element.isRequired
        },
        render() {
          return (
            <div>
              {this.props.children}
            </div>
          );
        }
      });

    const findTeamFunc = () => {
        Axios.post('http://localhost:3002/api/find', {
            teamName: findTeam,
        }).then((response) => {
            sendFindTeamArray(response.data);
        })
    };

    const addTeam = () => {
        Axios.post('http://localhost:3002/api/insert', {
            teamName: teamName,
            capRoom: capRoom,
            total_money_spent: total_money_spent,
            draft_picks: draft_picks
        }).then(() => {
            alert('success insert');
        })
    };

    const deleteTeam = (deleteTeamName) => {
        Axios.delete(`http://localhost:3002/api/delete/${deleteTeamName}`);
    };

    const updateTotalMoney = (movieName) => {
        Axios.put(`http://localhost:3002/api/update`, {
            teamName: teamName,
            total_money_spent: total_money_spent
        }).then(() => {
            alert('success insert');
        })
    };

    return (
        <Tabs selected = {0}>
            <Pane label='Tab one plz work'>
                <div className="App">
                    <h1> NBA Roster Engine</h1>
                    <div className="form">
                        {/* table */}
                        <h2>NBA Teams</h2>
                        <table id="team">
                            <tr>
                                <th>Team</th>
